/*
 * @name: 划词菜单(搜索+翻译+保存+转到)
 * @Author: 酷安@达蒙山
 * @version: 220504.18
 * @description: 划词搜索、划词翻译、保存选中文本、网址跳转
 * @include: *
 */
!function() {

  /*搜索引擎和翻译接口*/
  const apis = [

  {
    name: "百度搜索",
    api: "https://www.baidu.com/s?wd="
  },
  {
    name: "夸克搜索",
    api: "https://quark.sm.cn/s?q="
  },
  {
    name: "百度翻译",
    api: "https://fanyi.baidu.com/?tpltype=sigma#zd/zd/"
  },
  {
    name: "谷歌翻译",
    api: "https://translate.google.cn/?sl=auto&tl=auto&op=translate&text="
  }

  ];

  let swipesTimer,text='',

  openUrl=(a,b=text)=>window.open(a+b),

  blobDownload=(content,filename='.txt')=>{
    let alink=document.createElement('a');
    alink.download=filename;
    alink.style.display='none';
    alink.href=URL.createObjectURL(new Blob([content]));
    document.body.appendChild(alink);
    alink.click();
    document.body.removeChild(alink);
  },

  saveText=()=>{
    let save = document.createElement("span");
    save.innerHTML = "\u4fdd\u5b58\u9009\u4e2d";
    save.addEventListener("click",function(){blobDownload(text)});
    document.querySelector("#abcWords").appendChild(save);
    },

  goTo=()=>{
    let getUrl = text.match(/(https?:\/\/(\w[\w-]*\.)+[A-Za-z]{2,5}(?!\w)(:\d+)?(\/([\x21-\x7e]*[\w\/=])?)?|(\w[\w-]*\.)+(com|cn|org|net|info|tv|cc|gov|edu)(?!\w)(:\d+)?(\/([\x21-\x7e]*[\w\/=])?)?)/i)[0];
    if (getUrl) {
      let link = document.createElement("span");
      link.id = "abcLink";
      link.innerHTML = "\u8f6c\u5230";
      link.addEventListener("click",function(){
        -1==getUrl.indexOf("http") ? openUrl("http://", getUrl) : openUrl("", getUrl);
      });
      document.querySelector("#abcWords").appendChild(link);
    }
  },

  main=()=>{
    text = window.getSelection().toString().trim();    
    text&&(document.querySelector("#abcWords").style.display = "block",goTo());
  },

  swipes=()=>{
    document.querySelector("#abcWords").style.display = "none";
    document.querySelector("#abcLink")?.remove();
    clearTimeout(swipesTimer);
    swipesTimer = setTimeout(main, 750);
  }

  if (!document.querySelector("#abcWords")) {
    let adDiv = document.createElement("div");
    adDiv.id = "abcWords";
    adDiv.style.cssText = "display:none;width:100%;position:fixed;bottom:0px;z-index:9999999999;text-align:center;margin:4px auto;padding:4px;-webkit-tap-highlight-color:rgba(0,0,0,0);";
    document.body.appendChild(adDiv);
    let spanStyle = document.createElement("style");
    spanStyle.innerHTML = "#abcWords span{display:inline-block;background:#6a6a6a;color:#fff;font-size:20px;line-height:24px;margin:4px;padding:4px;border:1px solid #c5c5c5;border-radius:5px;}";
    document.body.appendChild(spanStyle);

    apis.forEach(i=>{
      let addApi = document.createElement("span");
      addApi.innerHTML = i.name;
      addApi.setAttribute("data-api", i.api);
      addApi.onclick = function(){
        openUrl(this.getAttribute("data-api"));
      };
      document.querySelector("#abcWords").appendChild(addApi);
    });

    saveText();

  }

  document.addEventListener("selectionchange", swipes);

}();
