/*
 * @name: 拼页
 * @Author: 酷安@达蒙山
 * @version: 220418.22
 * @description: 简单的手机网页拼接
 * @include: *
 */
!function(a,b){

 const 使用说明=`

需在屏幕从上到下、从右往左划出↙的手势，跨度大于屏幕尺寸1/3，呼出“拼页”按钮开关

推荐脚本

东方永页机 - 自动翻页脚本
https://greasyfork.org/zh-CN/scripts/438684-pagetual-perpetual-pages

页面自动拼接
https://greasyfork.org/zh-CN/scripts/389621-%E9%A1%B5%E9%9D%A2%E8%87%AA%E5%8A%A8%E6%8B%BC%E6%8E%A5

Super_preloaderPlus_one_New
https://greasyfork.org/zh-CN/scripts/436203-super-preloaderplus-one-new


`;

	const siteRules=[
		{
		"name":"网站名称",
		"match":/匹配url正则/,
		"selector":"下一页所在a标签的选择器",
		"auto":"自动拼页0关1开"
	},
	{
		"name":"百度搜索",
		"match":/(m|www)\.baidu\.com/,
		"selector":"a[class^='new-nextpage']",
		"auto":"0"
	}
		];

		let aSelector="";
		let addPage=()=>{

		let nextUrl="";
		let nowUrl;
		let nextTitle;
		const pageCoding=document.characterSet;
		const cachePage=document.createElement("div");
		cachePage.id="pinye-page";
		cachePage.style.cssText="display:none!important;";
		document.body.insertBefore(cachePage,document.body.firstChild);

		const doRemove=e=>{while(null!=e.nextSibling){e.parentNode.removeChild(e.nextSibling)}};

		const removeNextSibling=e=>{e.parentNode===document.querySelector("#pinye-page")||e.parentNode===document.body?doRemove(e):(doRemove(e),removeNextSibling(e.parentNode))};

		const doAdd=()=>{
    nowUrl!=location.href&&history.pushState(null,nextTitle,nowUrl);
    document.title=nextTitle;
    document.body.innerHTML+=document.querySelector("#pinye-page").innerHTML;
    ""!=nextUrl?getHtml(nextUrl):nowUrl="";
    };

		const getNextUrl=e=>{
   if(aSelector!=""){
    let getA=e.querySelector(aSelector);
    getA?(nextUrl=getA.href,removeNextSibling(getA.parentNode)):nextUrl="";
   }else{
    let aTags=e.getElementsByTagName("a");
    let aTagsL=aTags.length;
    const reg=/\u4e0b.?(\u9875|\u9801|\u8282|\u7bc0|\u7ae0|\u7bc7|\u5f20|\u5f35|\u5e45)|Next|>|\u00bb|\u2192/;
    if(aTagsL>0){
     for(let i=aTagsL-1;i>=0;i--){
      let atag=aTags[i];
      let aText=atag.innerText.trim().substr(0,5);
      let aUrl=atag.href;
      if(reg.test(aText)&&aUrl.indexOf("#")==-1){
       nextUrl=aUrl;
       removeNextSibling(atag.parentNode);
       break;
      }else{
      nextUrl="";
      }
     }
    }
   }
   };

		const getHtml=url=>{
   nowUrl=url;
   let xhr=new XMLHttpRequest();
   xhr.open("get",url,true);
   xhr.overrideMimeType("text/html;charset="+pageCoding);
   let nextHtml=()=>{
   if(4==xhr.readyState&&200==xhr.status){
   document.querySelector("#pinye-page").innerHTML=xhr.responseText.replace(/^\s+|\s+$/g,"").replace(/<!--.*?-->/g,"").replace(/>\s+?</g,"><").replace(/<style[\s\S]*?<\/style>/ig,"").replace(/<script[\s\S]*?<\/script>/ig,"").replace(/<iframe[\s\S]*?<\/iframe>/ig,"").match(/<body[\s\S]*<\/body>/i);
   nextTitle=xhr.responseText.match(/<title>([\s\S]*)<\/title>/i)[1];
   getNextUrl(document.querySelector("#pinye-page"));
   window.screen.height>=document.documentElement.scrollHeight&&doAdd();
     }
    }
   xhr.onreadystatechange=nextHtml;
   xhr.send();
  }

		getNextUrl(document);
		getHtml(nextUrl);
		document.addEventListener("scroll",()=>{
    let e=document.documentElement.scrollTop||document.body.scrollTop;
    nowUrl!=nextUrl&&e+window.screen.height>=document.documentElement.scrollHeight&&doAdd();
});

		}

/*匹配规则自动拼页*/
		for(let i=0;i<siteRules.length;i++){
				if(location.href.match(siteRules[i].match)){
						aSelector=siteRules[i].selector;
						"1"==siteRules[i].auto&&addPage();
						break;
				}
		}

/*脚本悬浮触发开关*/
	if(!document.querySelector("#cdTools")){let addDiv=document.createElement("div");addDiv.id="cdTools";addDiv.style.cssText="display:none;width:100%;position:fixed;bottom:0px;z-index:9999999999;text-align:center;margin:4px auto;padding:4px;-webkit-tap-highlight-color:rgba(0,0,0,0);";document.body.appendChild(addDiv);let btnStyle=document.createElement("style");btnStyle.innerHTML="#cdTools span{display:inline-block;background:#6a6a6a;color:#fff;font-size:20px;line-height:24px;margin:4px;padding:4px;border:1px solid #c5c5c5;border-radius:5px;}";addDiv.appendChild(btnStyle);let movex=window.screen.width/3,movey=window.screen.height/3,movex1,movex2,movey1,movey2;document.addEventListener("touchstart",e=>{movex1=e.changedTouches[0].clientX;movey1=e.changedTouches[0].clientY});document.addEventListener("touchmove",e=>{movex2=e.changedTouches[0].clientX;movey2=e.changedTouches[0].clientY;addDiv.style.display=movex1-movex2>movex&&movey2-movey1>movey?"block":"none"})}
	if(!document.getElementById(a)){let cdDiv=document.querySelector("#cdTools"),addBtn=document.createElement("span");addBtn.id=a;addBtn.innerHTML=b;addBtn.addEventListener("click",()=>{addPage();cdDiv.style.display="none"});cdDiv.appendChild(addBtn)}

}("jspinye","\u62fc\u9875");
